/**
 * Encode special characters in `value`.
 *
 * @param {string} value
 *   Value to encode.
 * @param {Options} [options]
 *   Configuration.
 * @returns {string}
 *   Encoded value.
 */
export function stringifyEntities(value: string, options?: Options | undefined): string;
/**
 * Encode special characters in `value` as hexadecimals.
 *
 * @param {string} value
 *   Value to encode.
 * @param {LightOptions} [options]
 *   Configuration.
 * @returns {string}
 *   Encoded value.
 */
export function stringifyEntitiesLight(value: string, options?: import("stringify-entities/lib/core").CoreOptions | undefined): string;
export type Options = import('stringify-entities/lib/core').CoreOptions & import('stringify-entities/lib/util/format-smart').FormatSmartOptions;
export type LightOptions = import('stringify-entities/lib/core').CoreOptions;
